import logo from './logo.svg';
import './App.css';
import {Route,Routes,BrowserRouter} from "react-router-dom"
import {HomePage, About, Projects, Game, Loader} from "./pages"
import {createContext, useEffect, useState} from 'react'
export const ThemeContext=createContext(null)


function App() {
  const [theme,setTheme]= useState("dark")
  const [loading,setLoading]= useState(0)
  const toggleTheme=()=>{
    setTheme((curr)=>(curr=== "light"? "dark":"light"));
    console.log(theme)
  }
  const toggleLoading=()=>{
    setLoading(1);
    console.log(loading)
  }
  useEffect(()=>{
    // getAllCategories();
   const timer= setTimeout(()=> setLoading(2), 1000); return()=> clearTimeout(timer)}, []
  )
  return (
    <ThemeContext.Provider value={{theme, toggleTheme}}>
       <div id={theme}> 
      {loading==1? ( <Loader/>):('')}
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<HomePage theme={theme} toggleTheme={toggleTheme} toggleLoading={toggleLoading} loading={loading}/>}/>
          <Route path="/about" element={<About  theme={theme} toggleTheme={toggleTheme}/>}/>
          <Route path="/projects" element={<Projects theme={theme} toggleTheme={toggleTheme} toggleLoading={toggleLoading} loading={loading}/>}/>

          <Route path="/game" element={<Game/>}/>
        </Routes>
      </BrowserRouter>
    </div>
    </ThemeContext.Provider>

  );
}

export default App;
