import { getValue } from '@testing-library/user-event/dist/utils'
import React from 'react'
import {createContext, useState} from 'react'
export const ThemeContext=createContext(null)


export function HomeHeader() {
  const [theme,setTheme]= useState("dark")
  const toggleTheme=()=>{
    setTheme((curr)=>(curr=== "light"? "dark":"light"));
    console.log(theme)
  }
  return (
<ThemeContext.Provider value={{theme, toggleTheme}}>
<div className='big-nav' id={theme}>
        <div className='nav1'>
            <div className="logo-box"><img src='images/icons/BlvckLogo.png' className='logo'/>
            </div>
            <div className="logo-word"
            >LVCK CODA</div>
        </div>

  <div className="App-logo" onClick={toggleTheme} checked={theme===dark} >
     <img src='images/icons/sun.png' style={{width:'100%',
    //  display:'none',
    }}/>
     <img src='images/icons/moon.png' style={{
      width:'50%',
      display:'none',
      }}/>
  </div>

        <div className='title'>
                DANIEL EWURUM
            
        </div>
      </div>

      </ThemeContext.Provider>
  )
}
